import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Folders.css';
import nice_plant_photo from '../img/nice_plant.jpg';
import water_button from '../img/water_button.svg';
import fertilizer_button from '../img/fertilizer_button.svg';
import add_plant_button from '../img/addPlantButton.svg';

const Folders = () => {
  const [plants, setPlants] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8000/app/plants/')
      .then(response => {
        setPlants(response.data);
      })
      .catch(error => {
        console.error('Error loading plants', error);
      });
  }, []);

  const handleDelete = async (plantId) => {
    try {
      await fetch(`http://localhost:8000/app/delete-plant/${plantId}/`, {
        method: 'DELETE',
      });
      setPlants(prevPlants => prevPlants.filter(plant => plant.id !== plantId));
      console.log('Plant deleted successfully');
    } catch (error) {
      console.error('Error deleting plant', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredPlants = plants.filter(plant =>
    plant.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="base-container">
      <main>
        <section className="plant_card">
            <img src={nice_plant_photo} alt="nice_plant_photo"></img>
              <div className="plantcard-name">select a plant</div>
              <div className="plantcard-water">Water</div>
              <div className="plantcard-fertilizer">Fertilize</div>
              <div className="plantcard-buttons">
                  <button className="water-button" data-plant-id="0">
                      <img src={water_button}  alt="water_the_plant"></img>
                  </button>
                 <button className="fertilizer-button" data-plant-id="0">
                      <img src={fertilizer_button} alt="fertilize_the_plant"></img>
                  </button>
              </div>
        </section>
        <section className="search_bar_and_folders">
        <section className="search_bar">
        <input
            type="text"
            placeholder="Search by name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </section>
        <section className="folders">
          {filteredPlants.map(plant => (
            <div key={plant.id} className="folder-container" data-plant-id={plant.id} data-water-last={plant.water_last} data-fertilizer-last={plant.fertilizer_last}>
              <img src={plant.image} alt={plant.name} />
              <div className="name-container">
                <h2>{plant.name}</h2>
                <div>
                  {plant.tags && plant.tags.map((tag, index) => (
                    <span key={index} className="tag">{tag}</span>
                  ))}
                </div>
                <div className="notification">
                  <button onClick={() => handleDelete(plant.id)}>Usuń</button>
                </div>
              </div>
            </div>
          ))}
          <div className="folder-container">
            <a href="/AddPlant">
              <img src={add_plant_button} alt="Add new plant" />
            </a>
            <div className="name-container">
              <h2>add new</h2>
              <div className="notification"></div>
            </div>
          </div>
        </section>
        </section>
      </main>
    </div>
  );
};

export default Folders;




