import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Folders.css';

const Folders = () => {
  const [plants, setPlants] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/app/plants/')
      .then(response => {
        setPlants(response.data);
      })
      .catch(error => {
        console.error('Error loading plants', error);
      });
  }, []);

  return (
    <div className="base-container">
      <main>
        <section className="folders">
          {plants.map(plant => (
            <div key={plant.id} className="folder-container" data-plant-id={plant.id} data-water-last={plant.water_last} data-fertilizer-last={plant.fertilizer_last}>
              <img src={`plant_images/${plant.image}`} alt={plant.name} />
              <div className="name-container">
                <h2>{plant.name}</h2>
                <div className="notification"></div>
              </div>
            </div>
          ))}
          <div className="folder-container">
            <a href="/AddPlant">
              <img src="public/img/addPlantButton.svg" alt="Add new plant" />
            </a>
            <div className="name-container">
              <h2>add new</h2>
              <div className="notification"></div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Folders;

